// Helper function to get upcoming deadlines sorted by date
export const getUpcomingDeadlines = (limit?: number): AdmissionDeadline[] => {
  const now = new Date();
  const upcoming = ADMISSION_DEADLINES
    .filter(d => new Date(d.applicationDeadline) >= now || d.status !== 'closed')
    .sort((a, b) => new Date(a.applicationDeadline).getTime() - new Date(b.applicationDeadline).getTime());
  return limit ? upcoming.slice(0, limit) : upcoming;
};

// Helper function to get deadlines by university
export const getDeadlinesByUniversity = (universityId: string): AdmissionDeadline[] => {
  return ADMISSION_DEADLINES.filter(d => d.universityId === universityId);
};

// Helper function to get highlighted/important deadlines
export const getHighlightedDeadlines = (): AdmissionDeadline[] => {
  return ADMISSION_DEADLINES.filter(d => d.isHighlighted || d.status === 'closing-soon');
};

// Helper function to get deadline status based on dates
export const getDeadlineStatus = (deadline: AdmissionDeadline): string => {
  const now = new Date();
  const deadlineDate = new Date(deadline.applicationDeadline);
  const startDate = new Date(deadline.applicationStartDate);
  const daysUntil = Math.ceil((deadlineDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));

  if (now < startDate) return 'upcoming';
  if (daysUntil < 0) return 'closed';
  if (daysUntil <= 7) return 'closing-soon';
  return 'open';
};

// Helper function to get days until deadline
export const getDaysUntilDeadline = (deadline: AdmissionDeadline): number => {
  const now = new Date();
  const deadlineDate = new Date(deadline.applicationDeadline);
  return Math.ceil((deadlineDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
};

export default ADMISSION_DEADLINES;