// Brand colors for different scholarship types
export const getScholarshipBrandColors = (type: ScholarshipData['type']): { primary: string; secondary: string } => {
  const colors: Record<string, { primary: string; secondary: string }> = {
    government: { primary: '#059669', secondary: '#D1FAE5' },
    private: { primary: '#7C3AED', secondary: '#EDE9FE' },
    institutional: { primary: '#2563EB', secondary: '#DBEAFE' },
    international: { primary: '#DC2626', secondary: '#FEE2E2' },
    need_based: { primary: '#F59E0B', secondary: '#FEF3C7' },
    merit_based: { primary: '#10B981', secondary: '#D1FAE5' },
    sports: { primary: '#EF4444', secondary: '#FEE2E2' },
    hafiz_e_quran: { primary: '#8B5CF6', secondary: '#EDE9FE' },
    special: { primary: '#EC4899', secondary: '#FCE7F3' },
    disabled: { primary: '#6366F1', secondary: '#E0E7FF' },
  };
  return colors[type] || { primary: '#6B7280', secondary: '#F3F4F6' };
};

// Get scholarships available for a specific university
export const getScholarshipsForUniversity = (universityId: string): ScholarshipData[] => {
  return SCHOLARSHIPS.filter(s => 
    s.targetAudience.includes(universityId) || 
    s.targetAudience.includes('ALL_PUBLIC') || 
    s.targetAudience.includes('ALL_HEC')
  );
};

// Check if scholarship is available at a university
export const isScholarshipAvailableAt = (scholarship: ScholarshipData, universityId: string): boolean => {
  return scholarship.targetAudience.includes(universityId) || 
         scholarship.targetAudience.includes('ALL_PUBLIC') || 
         scholarship.targetAudience.includes('ALL_HEC');
};

// Get availability text for a scholarship
export const getScholarshipAvailabilityText = (scholarship: ScholarshipData): string => {
  if (scholarship.targetAudience.includes('ALL_PUBLIC')) return 'All Public Universities';
  if (scholarship.targetAudience.includes('ALL_HEC')) return 'All HEC Recognized';
  if (scholarship.targetAudience.length > 5) return `${scholarship.targetAudience.length} Universities`;
  return scholarship.targetAudience.slice(0, 3).join(', ') + (scholarship.targetAudience.length > 3 ? '...' : '');
};

// Get specific university names for a scholarship
export const getScholarshipSpecificUniversities = (scholarship: ScholarshipData): string[] => {
  return scholarship.targetAudience.filter(t => !t.startsWith('ALL_'));
};

// Check if scholarship has broad availability
export const hasBroadAvailability = (scholarship: ScholarshipData): boolean => {
  return scholarship.targetAudience.includes('ALL_PUBLIC') || 
         scholarship.targetAudience.includes('ALL_HEC') ||
         scholarship.targetAudience.length > 10;
};

export default SCHOLARSHIPS;
